<template >
  
  <div class="card-row">
    <div 
      v-for="(card, index) in cards" 
      :key="index" 
      :ref="`card_${index}`"
      @mouseover="hoverCard(index)"
      @mouseout="hoverCard(-1)"
      class="card">
      
      <img class="card-image" href="/" :class="{'selected': isSelected(index)}"
           :src="card.image">
      
      <div class="card-footer">
        <p class="card-text">Articulo</p>
        <h3 class="card-title">{{card.title}}</h3>
        <p class="card-text">by 
          <span class="card-author" :class="{'selected': isSelected(index)}">{{card.marca}}</span>
        </p>
      </div>
    </div>
  </div>

</template>

<script>
const cards = [
  {title: 'Televisor 4K', marca: 'LG xl100',url:'/', image: 'https://images.samsung.com/is/image/samsung/es-uhdtv-nu7125-ue40nu7125kxxc-black-Black-101843347?$PD_GALLERY_L_JPG$'},
  {title: 'Camara 4k resolucion full ', marca: 'lg', image: 'data:image/jpeg;base64,'},
  {title: 'Lavadora full 100libras', marca: 'LG', image: 'https://media.aws.alkosto.com/media/catalog/product/cache/1/image/9df78eab33525d08d6e5fb8d27136e95/8/8/8806088921914_13.jpg'},
  {title: 'Televisor 4K', marca: 'LG xl100', image: 'https://images.samsung.com/is/image/samsung/es-uhdtv-nu7125-ue40nu7125kxxc-black-Black-101843347?$PD_GALLERY_L_JPG$'},
  {title: 'Camara 4k resolucion full ', marca: 'lg', image: 'data:image/jpeg;base64,'}
  
]


export default {
    data(){
      return{
    cards: cards,
    selectedCard: -1
      }
  },
  methods: {
    hoverCard(selectedIndex) {
      this.selectedCard = selectedIndex
      this.animateCards()
    },
    animateCards () {
      this.cards.forEach((card, index) => {
        const direction = this.calculateCardDirection(index, this.selectedCard)
       
      })
    },
    calculateCardDirection (cardIndex, selectedIndex) {
      if(selectedIndex === -1) {
        return 0
      }
      
      const diff = cardIndex - selectedIndex
      return diff === 0 ? 0 : diff/Math.abs(diff)
    },
    isSelected (cardIndex) {
      return this.selectedCard === cardIndex
    }
  }
}
</script>

